mapper.insert:
insert into t_alarm_log (id, create_time, update_time, 
  message, application_code, user_id, 
  error_log_id, send_time, channel_code
  )
values (#{id,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{message,jdbcType=VARCHAR}, #{applicationCode,jdbcType=VARCHAR}, #{userId,jdbcType=BIGINT}, 
  #{errorLogId,jdbcType=BIGINT}, #{sendTime,jdbcType=TIMESTAMP}, #{channelCode,jdbcType=VARCHAR}
  )

mapper.select:
select count(1) from t_alarm_log al left join t_application a on al.application_code = a.application_code left join t_user u on al.user_id = u.id left join t_error_log_record elr on al.error_log_id = elr.id where 1 = 1
and al.create_time >= #{beginDate,jdbcType=TIMESTAMP}
and al.create_time < #{endDate,jdbcType=TIMESTAMP}
and (u.user_name like CONCAT('%',#{keyWords},'%') or u.phone like CONCAT('%',#{keyWords},'%') or u.email like CONCAT('%',#{keyWords},'%'))

select al.id, al.create_time, al.update_time, al.message, al.application_code, al.user_id, al.error_log_id, al.send_time, al.channel_code, a.application_name, u.user_name, elr.log_message, elr.log_host_name, elr.log_file_path from t_alarm_log al left join t_application a on al.application_code = a.application_code left join t_user u on al.user_id = u.id left join t_error_log_record elr on al.error_log_id = elr.id where 1 = 1
and al.create_time >= #{beginDate,jdbcType=TIMESTAMP}
and al.create_time < #{endDate,jdbcType=TIMESTAMP}
and (u.user_name like CONCAT('%',#{keyWords},'%') or u.phone like CONCAT('%',#{keyWords},'%') or u.email like CONCAT('%',#{keyWords},'%'))
order by al.create_time desc

select

from t_alarm_log
where error_log_id = #{errorLogId,jdbcType=BIGINT}
  and
   channel_code = #{channelCode,jdbcType=VARCHAR}
