mapper.select:
select
 max(young_gc_collection_count) as young_gc_collection_count,
 max(full_gc_collection_count) as full_gc_collection_count,
 max(young_gc_collection_time) as young_gc_collection_time,
 max(full_gc_collection_time) as full_gc_collection_time
from t_jvm_gc
where
application_code = #{applicationCode,jdbcType=VARCHAR}

  and host_name = #{hostName,jdbcType=VARCHAR}

  and  create_time > #{beginDate,jdbcType=TIMESTAMP}

     and  create_time <= #{endDate,jdbcType=TIMESTAMP}
